        justify-content: center;
        display: flex;
        align-items: center;
        justify-content: center;
    }

    #header h1 a,
    #header h1 a::before,
    #header h2 span,
    #header h2 span::before {
        margin: 0;
        padding: 0;
        z-index: 0;
        display: block;
        text-align: center;
    }

    #header h1 {
        z-index: 1;
    }

