        -webkit-text-stroke: 0.25rem rgb(var(--swatch-text-dark));
    }

    #header h2 span::after {
        color: rgb(var(--swatch-headerh2-color));
        z-index: 1;
    }

    #search-top-box form[id="search-top-box-form"]:not(:focus-within) input[type="text"] {
